"use strict";window.onload=function(){$(".username").blur(function(){/^\w{4,9}$/.test($(".username").val())||layer.msg("用户名不合法")}),$(".password").blur(function(){/^\w{5,10}$/.test($(".password").val())||layer.msg("密码不合法")}),$("#login-btn").click(function(){var l=$(".username").val(),n=$(".password").val();return console.log(l),null==l?(layer.msg("请输入用户名"),!1):null==n?(layer.msg("请输入密码"),!1):void $.ajax({url:"/login",type:"post",data:{username:l,password:n},success:function(l){var n=JSON.parse(l);1==n.code&&(console.log("页面注册成功"),layer.confirm("恭喜您,注册成功,是否立即去登录?",{btn:["是的","我不要"]},function(l){layer.close(l),window.location="./login.html"},function(l){layer.close(l),layer.msg("好的")})),0==n.code&&layer.msg("用户名已注册!")}})})};